import React, { useEffect, useState } from 'react'

// Import created components
import ControlBar from './ControlBar'
import NoteEditor from './NoteEditor'

// Import note operation functions
import { addNote, 
  getNotes, 
  updateNote, 
  deleteNote } from '../../services/noteFunctionalitiesServices'

// Import stylesheet
import './Home.css'

export default function Home({ user }) {

  // State var to hold the note currently displayed by the editor
  const [selectedNote, setSelectedNote] = useState({
    userID: user._id,
    noteID: 'new_note',
    title: '',
    content: '',
    date: new Date().toLocaleString('en-US', { 
      timeZone: 'America/New_York', 
      year: '2-digit', 
      month: '2-digit', 
      day: '2-digit', 
      hour: '2-digit', 
      minute: '2-digit' })
  })

  const [selectedInd, setSelectedInd] = useState(-1)
  const [userNotes, setUserNotes] = useState([])

  // Update functions for the selected note
  const handleTitleChange = (event) => {
    console.log('New Title', event.target.value)
    setSelectedNote({
      ...selectedNote,
      title: event.target.value,
    });
  };

  const handleContentChange = (event) => {
    console.log('New Content', event.target.value)
    setSelectedNote({
      ...selectedNote,
      content: event.target.value,
    });
  };

  // Save note function
  const handleSave = async () => {
    // case where we are creating a new note
    if (selectedNote.noteID == 'new_note'){
      const response = await addNote(
        selectedNote.userID,
        selectedNote.date,
        selectedNote.title,
        selectedNote.content)
      setUserNotes(response)
    } else {
    // case where we are creating a new note

    }
    // update the users stored notes
  }

  const handleSelectNote = (index) => {
    const note = userNotes[index]
    setSelectedNote({
      ...selectedNote,
      noteID: note._id,
      content: note.content,
      date: note.date,
      title: note.title
    })
    setSelectedInd(index)
  }

  const handleGetNotes = async () => {
    const userNotes = await getNotes(user._id)
    console.log('existing userNotes: ', userNotes)
    // append an empty note to the end of the array
    userNotes.push({
      userID: user._id,
      noteID: 'new_note',
      title: '',
      content: '',
      date: new Date().toLocaleString('en-US', { 
        timeZone: 'America/New_York', 
        year: '2-digit', 
        month: '2-digit', 
        day: '2-digit', 
        hour: '2-digit', 
        minute: '2-digit' 
      })
    })
    setUserNotes(userNotes)
  }


  const handleDeleteNote = async () => {
    const response = await deleteNote(selectedNote.userID, selectedNote.noteID)
    handleGetNotes()
  }

  // useEffect to generate a new list of notes each time:
  // - when a note is updated
  // - when a note is added or deleted
  // - when the component is initially rendered
  useEffect(() => {
    handleGetNotes()
  }, [])

  return (
    <div className='homeContainer'>
      <ControlBar 
        notes={userNotes}
        selected_ind={selectedInd}
        selectNote={handleSelectNote} 
        handleSave={handleSave}
        handleDeleteNote={handleDeleteNote}/>
      <NoteEditor 
        note={selectedNote}
        handleTitleChange={handleTitleChange}
        handleContentChange={handleContentChange}/>
    </div>
  )
}
